'use strict'

const koolShell = require('..')
const koolSpinner = require('../plugins/spinner')
const koolLog = require('../plugins/log')

const sh = koolShell()
  .use(koolLog)
  .use(koolSpinner)

const TICK_DELAY = 500
const spinner = sh.spinner({
  title: 'spinning... %s',
  delay: 100,
})

spinner.resume()
setTimeout(tick, TICK_DELAY)

let count = 0
function tick () {
  if (++count < 10) {
    spinner.log(`${randomEmoji()} ${randomEmoji()} ${randomEmoji()}`)
    setTimeout(tick, TICK_DELAY)
  } else spinner.pause(true)
}

